        self.titulo = titulo
        self.autor = autor
        self.categoria = categoria


class Leitor:
    def __init__(self, nome):
        self.nome = nome
        self.preferencias = {}

    def adicionar_preferencia(self, livro, valor):
        if livro in self.preferencias:
            self.preferencias[livro] += valor
        else:
            self.preferencias[livro] = valor

    def get_preferencias(self):
        return self.preferencias


def calcular_recomendacoes(lidos, preferencias):
    # Cria uma lista de livros recomendados
    recomendacao = []

    # Loop por todos os livros lidos pelo usuario
    for livro in lidos:
        # Verifica se o livro foi adicionado na lista das preferencias do usuario
        if livro in preferencias.get('livros', {}):
            # Se sim, calcula a pontuação do livro
            pontuacao = preferencias['livros'][livro]

            # Adiciona o livro à lista de recomendações
            recomendacao.append((livro, pontuacao))

        # Caso contrário, verifica se há algum livro semelhante ao livro atual
        elif livro.categoria == 'Ficcao':
            similar_livros = [lido for lido in lidos if lido not in recommendacao and lido.categoria == 'Ficcao']
            if similar_livros:
                # Calcula a pontuação média dos livros similares
                pontuacao_similar = sum([preferencias.get('livros', {}).get(similar_livro, 0) for similar_livro in similar_livros]) / len(similar_livros)

                # Adiciona o livro à lista de recomendações
                recomendacao.append((livro, pontuacao_similar))

    # Ordena a lista de recomendações pela pontuação
    recomendacao.sort(key=lambda x: x[1], reverse=True)

    return recomendacao[:5]


